package net.exclaimindustries.paste.braket.shared;

import com.google.gwt.thirdparty.guava.common.io.BaseEncoding;
import com.googlecode.objectify.annotation.Cache;
import com.googlecode.objectify.annotation.Entity;
import com.googlecode.objectify.annotation.Id;

@Entity
@Cache
public class Image {
	
	public static Image UNKNOWN = new Image(0, "image/png", Image.UNKNOWN_DATA);
	
	private static byte[] UNKNOWN_DATA = BaseEncoding.base64().decode("");
	
	@Id
	private Long id;
	
	private String type;
	
	private byte[] data;

	private Image(long id, String type, byte[] data) {
		this.id = id;
		this.type = type;
		this.data = data;
	}
	
	public Image(String type, byte[] data) {
		this.id = null;
		this.type = type;
		this.data = data;
	}
	
	public String getType() {
		return this.type;
	}
	
	public byte[] getData() {
		return this.data;
	}
	
}
